package com.lonar.vendor.vendorportal.model;

public class SendBroadCastEmail {
	
	String companyCategory;
	Long divisionId;
	String status;
	String subject;
	String message;
	String emailFlag;
	Long companyId;
	Long employeeId;
	Long lastUpdatedBy;
	String attachmentPath;
	
	public String getCompanyCategory() {
		return companyCategory;
	}
	public void setCompanyCategory(String companyCategory) {
		this.companyCategory = companyCategory;
	}
	public Long getDivisionId() {
		return divisionId;
	}
	public void setDivisionId(Long divisionId) {
		this.divisionId = divisionId;
	}
	public String getStatus() {
		return status;
	}
	public void setStatus(String status) {
		this.status = status;
	}
	public String getSubject() {
		return subject;
	}
	public void setSubject(String subject) {
		this.subject = subject;
	}
	public String getMessage() {
		return message;
	}
	public void setMessage(String message) {
		this.message = message;
	}
	public String getEmailFlag() {
		return emailFlag;
	}
	public void setEmailFlag(String emailFlag) {
		this.emailFlag = emailFlag;
	}
	public Long getCompanyId() {
		return companyId;
	}
	public void setCompanyId(Long companyId) {
		this.companyId = companyId;
	}
	public Long getLastUpdatedBy() {
		return lastUpdatedBy;
	}
	public void setLastUpdatedBy(Long lastUpdatedBy) {
		this.lastUpdatedBy = lastUpdatedBy;
	}
	public Long getEmployeeId() {
		return employeeId;
	}
	public void setEmployeeId(Long employeeId) {
		this.employeeId = employeeId;
	}
	public String getAttachmentPath() {
		return attachmentPath;
	}
	public void setAttachmentPath(String attachmentPath) {
		this.attachmentPath = attachmentPath;
	}
	@Override
	public String toString() {
		return "SendBroadCastEmail [companyCategory=" + companyCategory + ", divisionId=" + divisionId + ", status="
				+ status + ", subject=" + subject + ", message=" + message + ", emailFlag=" + emailFlag + ", companyId="
				+ companyId + ", employeeId=" + employeeId + ", lastUpdatedBy=" + lastUpdatedBy + ", attachmentPath="
				+ attachmentPath + "]";
	}
	
	
	
}
